const max = 10;
const min = 0;
let randomIndex = Math.floor(Math.random() * (max - min) + min);

export function WordGeneratorReducer(state = initState, action) {
    if (action.type === 'EASY') {
        const easyWords = ["Cable", "Faces", "Label", "Nacho", "Wacky", 
                       "Yacht", "Adult", "Grass", "Water", "Tower"];


        // let randomIndex = Math.floor(Math.random() * (max - min) + min);

