import React, { useState } from 'react';
import { useSelector, shallowEqual, useDispatch} from 'react-redux';
import './InputBox.css';


export default function InputBox(props) {
    const [candidate, setCandidate] = useState("");

    //const [array_of_guesses, updateGuessArray] = useState([]);

    let userGuess = candidate;

    const dispatch = useDispatch();

    function sendWordToHistory() {
        const action = {
            type: 'EVALUATE_WORD',
            value: userGuess,
        }

        dispatch(action);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("userGuess: " + userGuess);
        sendWordToHistory();
        //sendWordToHistory();
        // console.log({candidate});
    }

    // function evaluateCandidate() {
    //     // let userGuess = candidate;
    //     sendWordToHistory();
    //     console.log("newWord: " + userGuess);
    //     //console.log(toString(newWord));
    // }

    return (
        <div>
            <form onSubmit={e => {handleSubmit(e)}}>
                    <label className="text">
                        Enter Word:
                        <input 
                            type="text"
                            value={candidate}
                            onChange={(e) => setCandidate(e.target.value.trim())}
                        ></input>
                    </label>
                    <input type="submit" value="Submit"></input>


                </form>
                {/* <div>Here: {curWord}</div> */}
        </div>
    );



}
