import React, { useState } from 'react';
import { useSelector, shallowEqual, useDispatch} from 'react-redux';
import './InputBox.css';


export default function InputBox(props) {
    const [candidate, setCandidate] = useState("");
    const [validLength, setValidation] = useState("");

    //const [array_of_guesses, updateGuessArray] = useState([])

    const dispatch = useDispatch();

    function sendWordToHistory() {
        const action = {
            type: 'EVALUATE_WORD',
            value: candidate,
        }

        dispatch(action);
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("userGuess: " + candidate);
        sendWordToHistory();
    }


    function checkValidInput(state) {
        console.log("in function")
        if (state.invalidWord) {
            console.log("in if");
            return state.invalidWordComment;
        } else {
            console.log("in else");
            return state.validWordComment;
        }
    }

    const checkLength = useSelector(checkValidInput, shallowEqual);

    return (
        <div>
            <form onSubmit={e => {handleSubmit(e)}}>
                    <label className="text">
                        Enter Word:
                        <input 
                            type="text"
                            onChange={(e) => setCandidate(e.target.value.trim())}
                        ></input>
                    </label>
                    <input type="submit" value="Submit"></input>
            </form>
        </div>
    );



}
