// Initial states
const initState = {
    current_diff: "",
    easy_word: "",
    med_word: "",
    hard_word: "",
    easy_history: [],
    med_history: [],
    hard_history: [],
    invalidWordLength: false,
    easyattemptsRemaining: -1,
    medattemptsRemaining: -1,
    hardattemptsRemaining: -1,
    invalidWord: false,
}

const max = 10;
const min = 0;

export function WordGeneratorReducer(state = initState, action) {
    //Starting a new easy game - either clicks the easy link for the first time or clicks the new game button
    if (action.type === 'EASY' && (state.easy_word === "" || action.newGame === 'YES')) {
        const easyWords = ["Cable", "Faces", "Label", "Nacho", "Wacky", 
                       "Yacht", "Adult", "Grass", "Water", "Tower"];

        let randomIndexEasy = Math.floor(Math.random() * (max - min) + min);

        console.log("random selected word: " + easyWords[randomIndexEasy]);

        return {
            ...state, 
            current_diff: "EASY",
            easy_word: easyWords[randomIndexEasy].toUpperCase(),
            easy_history: [],
            easyattemptsRemaining: action.easyattempts,
        }
    }
    // Switches to the easy game tab, but game has already started
    else if (action.type === 'EASY') {

        return {
            ...state, 
            current_diff: "EASY",
        }
    }
    //Starting a new medium game - either clicks the medium link for the first time or clicks the new game button
    if (action.type === 'MEDIUM' && (state.med_word === "" ||  action.newGame === 'YES')) {
        const medWords = ["Acting", "Amount", "Became", "Belief", "Breath", 
                          "Design", "Doctor", "Enable", "Fabric", "Golden"];
    

        let randomIndexMedium = Math.floor(Math.random() * (max - min) + min);

        console.log(medWords[randomIndexMedium]);
        return {
            ...state,
            current_diff: "MEDIUM",
            med_word: medWords[randomIndexMedium].toUpperCase(),
            med_history: [],
            medattemptsRemaining: action.medattempts,
        }
    }
    // Switches to the med game tab, but game has already started
    else if (action.type === 'MEDIUM') {

        return {
            ...state, 
            current_diff: "MEDIUM",
        }
    }

    //Starting a new hard game - either clicks the hard link for the first time or clicks the new game button
    if (action.type === 'HARD' && (state.hard_word === "" || action.newGame === 'YES')) {
        const hardWords = ["Fuzzier", "Drizzle", "Quibble", "Oxidize", "Tickbox", "Complex", "Squeeze", "Jumping", "Quickley", "Eagerly"];

        let randomIndexHard = Math.floor(Math.random() * (max - min) + min);

        console.log(hardWords[randomIndexHard]);

        return {
            ...state, 
            current_diff: "HARD",
            hard_word: hardWords[randomIndexHard].toUpperCase(),
            hard_history: [],
            hardattemptsRemaining: action.hardattempts,
        }

    }
    // Switches to the hard game tab, but game has already started
    else if (action.type === 'HARD') {

        return {
            ...state, 
            current_diff: "HARD",
        }
    }


    if (action.type === "EVALUATE_WORD") {
        console.log("eval function called");
        let newWord = action.value.toString();

       
        // Easy
        if (state.current_diff === "EASY" && newWord.length === 5){
            
            if (!five_letter_words.includes(newWord.toUpperCase())){
                return {
                    ...state,
                    invalidWord: true,
                }
            } else {
            return {
                ...state,
                easy_history: [...state.easy_history, action.value],
                invalidWordLength: false,
                easyattemptsRemaining: state.easyattemptsRemaining - 1,
                invalidWord: false,
            }
            }
        }
        // Medium
        else if (state.current_diff === "MEDIUM" && newWord.length === 6){

            if (!six_letter_words.includes(newWord.toUpperCase())){
                return {
                    ...state,
                    invalidWord: true,
                }
            } else {
            return {
                ...state,
                med_history: [...state.med_history, action.value],
                invalidWordLength: false,
                medattemptsRemaining: state.medattemptsRemaining - 1,
                invalidWord: false,
            }
        }
        }
        // Hard
        else if (state.current_diff === "HARD" && newWord.length === 7){
            console.log(state.history);

            if (!seven_letter_words.includes(newWord.toUpperCase())){
                return {
                    ...state,
                    invalidWord: true,
                }
            } else {
            return {
                ...state,
                hard_history: [...state.hard_history, action.value],
                invalidWordLength: false,
                hardattemptsRemaining: state.hardattemptsRemaining - 1,
                invalidWord: false,
            }
        }
        }
        // Not valid
        else {
            // Invalid! The word must be of length {state.word.length}.
            return {
                ...state,
                invalidWordLength: true,
                invalidWord: false,
                // attemptsRemaining: attemptsRemaining,
            }
        }
    } else {
        return {
            ... state,
            attemptsRemaining: state.attemptsRemaining,
        }
    }
        
    
    return state;
}

